import Header from "./Header";
import Footer from "./Footer";
import Story from "./Story";
import Form from "./Form";
import { useState, useEffect, useRef } from "react";

function App() {
  const [character1, setCharacter1] = useState("");
  const [character2, setCharacter2] = useState("");
  const [writer, setWriter] = useState("");
  const [input, setInput] = useState("");
  const [reply, setReply] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const handleSend = async () => {
      setLoading(true);
      try {
        const response = await fetch(
          "https://api.openai.com/v1/chat/completions",
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer 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`, // âš ï¸ Use env vars in production
            },
            body: JSON.stringify({
              model: "gpt-3.5-turbo",
              messages: [{ role: "user", content: input }],
            }),
          },
        );

        const data = await response.json();
        const message = data.choices?.[0]?.message?.content || "[No response]";
        setReply(message);
      } catch (error) {
        setReply("Error: " + error.message);
      } finally {
        setLoading(false);
      }
    };

    if (input) handleSend();
    return () => {};
  }, [input]); // ðŸ‘ˆ Dependency array

  return (
    <div className="App">
      <Header title={"AI generated storyline"} />
      <div className="container">
        <Form
          character1={character1}
          character2={character2}
          writer={writer}
          loading={loading}
          setCharacter1={setCharacter1}
          setCharacter2={setCharacter2}
          setWriter={setWriter}
          setInput={setInput}
        />
        <Story story={reply} />;
      </div>
      <Footer />
    </div>
  );
}

export default App;
